import "./App.css";
import * as React from "react";
import { Switch, Route } from "react-router-dom";
import { HashRouter as Router } from "react-router-dom";
import Header from "./components/layout/Header";
import Footer from "./components/layout/Footer";
import HomePage from "./components/pages/Home/HomePage";
import LecturePage from "./components/pages/Lectures/LecturePage";
import LectureIntro from "./components/pages/Lectures/LectureIntro";
import Aboutus from "./components/pages/Aboutus/Aboutus";
import AboutTeam from "./components/pages/Aboutus/AboutTeam";
import { useHistory } from "react-router-dom";
import Sidebar from "./components/layout/Sidebar";

function App() {
  const [showSidebar, setShowSidebar] = React.useState(false);

  function ScrollToTop() {
    const history = useHistory();
    React.useEffect(() => {
      const unlisten = history.listen((location, action) => {
        if (action !== "POP") {
          window.scrollTo(0, 0);
        }
      });
      return () => unlisten();
    }, [history]);
    return null;
  }
  return (
    <div className="App">
      <Router>
        <Header />
        <ScrollToTop />
        <div className="App">
          <Switch>
            <Route path="/about">
              <Aboutus />
            </Route>
            <Route path="/team">
              <AboutTeam />
            </Route>
            <Route path="/lectures/:id/:lecture">
              <LecturePage />
            </Route>
            <Route path="/lectures">
              <LectureIntro />
            </Route>
            <Route path={"*"}>
              <HomePage />
            </Route>
          </Switch>
        </div>
        <Footer />
        <div className="relative inline-block md:hidden">
          <button
            onClick={() => setShowSidebar(!showSidebar)}
            className="fixed bottom-10 right-5 bg-gray-100 text-white p-2 rounded-full m-2 z-50"
          >
            <img
              src="data:image/png;base64,"
              alt=""
            />{" "}
          </button>
          <Sidebar showSidebar={showSidebar} setShowSidebar={setShowSidebar} />
        </div>
      </Router>
    </div>
  );
}

export default App;
